INSERT INTO `dienthoai` (`idDT`, `idLoai`, `idCL`, `TenDT`, `MoTa`, `NgayCapNhat`, `Gia`, `GiaKM`, `urlHinh`, `baiviet`, `SoLanXem`, `SoLuongTonKho`, `GhiChu`, `SoLanMua`, `AnHien`, `Hot`) VALUES
(625, 41, 1, 'HTC DESIRE', 'Màn hình AMOLED 16 triệu màu, rộng 3.7 inches\r\nMáy ảnh 5.0 MP (2592 x 1944 pixels)\r\nĐài FM tích hợp: Có\r\nNghe nhạc: WMA, WAV, AAC+, MP3\r\nXem Phim: WMV, 3GP, MP4\r\nKết nối 3G: HSDPA, 7.2 Mbps; HSUPA, 2 Mbps, Wifi Wi-Fi 802.11 b/g, GPS: A-GPS, Bluetooth: Có,', '0000-00-00', 12300000, 12300000, 'HTC_Desire_b.jpg', '<div class=\"newstitle\">\r\n<h1>Desire - Chiếc Android mạnh mẽ nhất của HTC</h1>\r\n</div>\r\n<div class=\"newsintro\">\r\n<p>HTC Desire l&agrave; di động chạy Android mạnh mẽ nhất của HTC hiện nay với vi xử l&yacute; 1GHz, m&agrave;n h&igrave;nh AMOLED 3,7 inch v&agrave; nhiều trang bị kh&aacute;c.</p>\r\n</div>\r\n<p class=\"Normal\">Đ&acirc;y l&agrave; một trong số ba mẫu di động được HTC c&ocirc;ng bố đầu năm nay b&ecirc;n cạnh HD Mini v&agrave; Legend. Trong đ&oacute;, Legend đ&atilde; b&aacute;n tại Việt Nam, HD Mini sẽ xuất hiện trong thời gian tới, c&ograve;n Desire c&oacute; thể sang th&aacute;ng 5 mới c&oacute; mặt.</p>\r\n<p class=\"Normal\">Desire được xem l&agrave; một phi&ecirc;n bản kh&aacute;c của Nexus One được HTC sản xuất cho Google, tuy nhi&ecirc;n m&aacute;y thay đổi một v&agrave;i điểm trong thiết kế, ngo&agrave;i ra dung lượng RAM được cải thiện v&agrave; th&ecirc;m c&aacute;c t&iacute;nh năng như đ&agrave;i FM.<br /><br /><strong><span style=\"color: #ff0000;\">&gt;&gt;&gt;</span> Xem chi tiết th&ocirc;ng tin, h&igrave;nh ảnh, video HTC Desire &lt;<a href=\"http://www.mainguyen.vn/dienthoai/chitiet/dien-thoai-htc-desire-1522-19.html\" target=\"_blank\">tại đ&acirc;y</a>&gt;</strong><br /><span style=\"color: #3366ff;\">HTC Desire ch&iacute;nh h&atilde;ng dự kiến sẽ c&oacute; h&agrave;ng tại Mai Nguy&ecirc;n v&agrave;o đầu th&aacute;ng 5/2010.</span></p>\r\n<p class=\"Normal\"><em>Dưới đ&acirc;y l&agrave; một số h&igrave;nh ảnh thực tế Desire.</em></p>\r\n<table style=\"width: 77%;\" border=\"0\" cellspacing=\"2\" cellpadding=\"2\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td class=\"BoxLink\"><img style=\"border: 1px solid black;\" src=\"http://sohoa.vnexpress.net/Files/Subject/3B/9B/21/6C/1.jpg\" alt=\"\" width=\"480\" height=\"360\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\">M&aacute;y b&aacute;n ra đi k&egrave;m c&aacute;c phụ kiện như sạc, c&aacute;p USB, tai  nghe.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\"><img style=\"border: 1px solid black;\" src=\"http://sohoa.vnexpress.net/Files/Subject/3B/9B/21/6C/2.jpg\" alt=\"\" width=\"480\" height=\"360\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\">Desire c&oacute; k&iacute;ch thước 119 x 60 x 11,9 mm, nặng 135  gram.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\"><img style=\"border: 1px solid black;\" src=\"http://sohoa.vnexpress.net/Files/Subject/3B/9B/21/6C/3.jpg\" alt=\"\" width=\"480\" height=\"407\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\">Mặt trước model n&agrave;y l&agrave; m&agrave;n h&igrave;nh cảm ứng đa điểm  AMOLED rộng 3,7 inch, độ ph&acirc;n giải 480 x 800 pixel.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\"><img style=\"border: 1px solid black;\" src=\"http://sohoa.vnexpress.net/Files/Subject/3B/9B/21/6C/4.jpg\" alt=\"\" width=\"480\" height=\"345\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\">M&aacute;y trang bị kết nối Wi-Fi chuẩn b/g, HSDPA, 7,2  Mb/gi&acirc;y; HSUPA, 2 Mb/gi&acirc;y, Bluetooth với A2DP, GPS, hỗ trợ A-GPS.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\"><img style=\"border: 1px solid black;\" src=\"http://sohoa.vnexpress.net/Files/Subject/3B/9B/21/6C/5.jpg\" alt=\"\" width=\"480\" height=\"319\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\">So d&aacute;ng với Xperia X10, Samsung Galaxy S v&agrave; iPhone  3GS.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\"><img style=\"border: 1px solid black;\" src=\"http://sohoa.vnexpress.net/Files/Subject/3B/9B/21/6C/6.jpg\" alt=\"\" width=\"480\" height=\"298\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\">Giao diện của 4 model n&agrave;y.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\"><img style=\"border: 1px solid black;\" src=\"http://sohoa.vnexpress.net/Files/Subject/3B/9B/21/6C/7.jpg\" alt=\"\" width=\"480\" height=\"360\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\">Kh&aacute;c với Nexus One, Desire sử dụng n&uacute;t điều khiển quang học giống Legend, c&aacute;c n&uacute;t bấm kh&aacute;c như Home, Menu, back v&agrave; n&uacute;t t&igrave;m kiếm được đưa xuống.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\"><img style=\"border: 1px solid black;\" src=\"http://sohoa.vnexpress.net/Files/Subject/3B/9B/21/6C/8.jpg\" alt=\"\" width=\"480\" height=\"275\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\">Cạnh tr&aacute;i l&agrave; ph&iacute;m tăng giảm &acirc;m lượng.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\"><img style=\"border: 1px solid black;\" src=\"http://sohoa.vnexpress.net/Files/Subject/3B/9B/21/6C/9.jpg\" alt=\"\" width=\"480\" height=\"302\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\">N&uacute;t nguồn v&agrave; giắc cắm tai nghe 3,5 mm ở tr&ecirc;n đỉnh.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\"><img style=\"border: 1px solid black;\" src=\"http://sohoa.vnexpress.net/Files/Subject/3B/9B/21/6C/10.jpg\" alt=\"\" width=\"480\" height=\"318\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\">M&aacute;y sử dụng cổng giao tiếp microUSB.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\"><img style=\"border: 1px solid black;\" src=\"http://sohoa.vnexpress.net/Files/Subject/3B/9B/21/6C/11.jpg\" alt=\"\" width=\"480\" height=\"336\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\">Mặt sau của Desire l&agrave; camera 5 Megapixel, hỗ trợ chế độ tự động cạnh n&eacute;t, nhận dạng khu&ocirc;n mặt, geotagging. Ngo&agrave;i ra, model n&agrave;y c&ograve;n c&oacute; đ&egrave;n flash LED, quay video WVGA 800 x 480 pixel ở 15 khung h&igrave;nh/gi&acirc;y. M&aacute;y kh&ocirc;ng c&oacute; camera thứ hai để đ&agrave;m thoại video.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\"><img style=\"border: 1px solid black;\" src=\"http://sohoa.vnexpress.net/Files/Subject/3B/9B/21/6C/12.jpg\" alt=\"\" width=\"480\" height=\"360\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\">Pin theo m&aacute;y c&oacute; dung lượng 1400 mAh.</td>\r\n</tr>\r\n</tbody>\r\n</table>', 213, 200, NULL, 126, 1, 0),
(626, 41, 1, 'HTC MAGIC', 'Kết nối 3.5G tốc độ cao và công nghệ Wi-Fi cùng khả năng truyền tải liên tục\r\nTăng khả năng duyệt web với việc mở nhiều cửa sổ cùng lúc và tìm từ khoá ngay trên trang\r\nMàn hình cảm ứng chạm 3.2-inch độ phân giải HVGA (320x480 pixel)\r\nMáy ảnh 3.2 Megapixel', '0000-00-00', 11399000, 11399000, '000000000HTC_Magic_b.jpg', '<p class=\"Title\">HTC Magic - di động Google thứ hai kh&ocirc;ng b&agrave;n ph&iacute;m</p>\r\n<p class=\"Lead\">HTC Magic l&agrave; điện thoại chạy hệ điều h&agrave;nh Android thứ hai, m&aacute;y c&oacute; cấu h&igrave;nh giống <a class=\"Lead\" href=\"http://sohoa.vnexpress.net/News/Dien-thoai/2009/02/3B9AFB9F/\">Dream</a> đi trước nhưng thay đổi lớn l&agrave; b&agrave;n ph&iacute;m QWERTY lược bỏ v&agrave; thiết kế mỏng hơn.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://sohoa.vnexpress.net/Files/Subject/3B/9A/FC/12/android-magic-top-new.jpg\" border=\"1\" alt=\"Magic kh&ocirc;ng c&oacute; b&agrave;n ph&iacute;m như Dream. Ảnh: HTC.\" width=\"400\" height=\"327\" align=\"center\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Magic kh&ocirc;ng c&oacute; b&agrave;n ph&iacute;m như Dream. Ảnh: <em>HTC.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">H&ocirc;m qua (17/2), ng&agrave;y thứ hai của Hội nghị Di động Thế giới MWC 2009, HTC ra mắt chiếc di động thứ hai chạy hệ điều h&agrave;nh Android của Google. Thiết bị mới được gọi l&agrave; HTC Magic, được sản xuất d&agrave;nh ri&ecirc;ng cho nh&agrave; mạng Vodafone.</p>\r\n<p class=\"Normal\">Thiết kế của model mới c&oacute; v&agrave;i n&eacute;t kh&aacute;c biệt với \"&ocirc;ng anh\" HTC Dream đi trước, m&aacute;y bỏ b&agrave;n ph&iacute;m QWERTY trượt ngang, thay v&agrave;o đ&oacute; l&agrave; kiểu d&aacute;ng mảnh mai hơn. M&agrave;n h&igrave;nh cảm ứng ho&agrave;n to&agrave;n của Magic rộng 3,2 inch, tương đương với Dream, chạy tr&ecirc;n 4 băng tần GSM, GPRS, EDGE v&agrave; hai băng tần UMTS, hoạt động tốt với Wi-Fi cũng như khả năng định vị to&agrave;n cầu GPS.</p>\r\n<p class=\"Normal\">Những t&iacute;nh năng kh&aacute;c của Magic như camera 3,2 Megapixel, hỗ trợ chế độ tự động canh n&eacute;t, kết nối Bluetooth, USB. M&aacute;y chạy tr&ecirc;n bộ vi xử l&yacute; Qualcomm 7201A tốc độ 528 Mhz, RAM 192 MB. Trong khi đ&oacute;, bộ nhớ ROM l&ecirc;n tới 512 MB, gấp hai lần so với Dream.</p>\r\n<p class=\"Normal\">HTC Magic về cơ bản được x&acirc;y dựng từ Dream nhưng bỏ đi b&agrave;n ph&iacute;m. Đ&acirc;y l&agrave; model mang t&ecirc;n m&atilde; HTC Sapphire từng xuất hiện trong bản danh s&aacute;ch sản phẩm năm 2009 của HTC bị lộ trước đ&acirc;y.</p>\r\n<p class=\"Normal\">H&atilde;ng vẫn chưa đưa ra th&ocirc;ng tin thời điểm c&oacute; mặt v&agrave; gi&aacute; b&aacute;n ch&iacute;nh thức của model n&agrave;y.</p>\r\n<p class=\"Normal\">H&igrave;nh ảnh ch&iacute;nh thức của HTC Magic.</p>\r\n<table border=\"0\" cellspacing=\"3\" cellpadding=\"3\" width=\"405\" align=\"center\" bgcolor=\"#dfdfdf\">\r\n<tbody>\r\n<tr>\r\n<td class=\"BoxLink\"><img src=\"http://sohoa.vnexpress.net/Files/Subject/3B/9A/FC/12/1.jpg\" border=\"1\" alt=\"\" width=\"400\" height=\"531\" align=\"center\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\"><img src=\"http://sohoa.vnexpress.net/Files/Subject/3B/9A/FC/12/2.jpg\" border=\"1\" alt=\"\" width=\"400\" height=\"276\" align=\"center\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\"><img src=\"http://sohoa.vnexpress.net/Files/Subject/3B/9A/FC/12/3.jpg\" border=\"1\" alt=\"\" width=\"400\" height=\"190\" align=\"center\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\"><img src=\"http://sohoa.vnexpress.net/Files/Subject/3B/9A/FC/12/4.jpg\" border=\"1\" alt=\"\" width=\"400\" height=\"437\" align=\"center\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\"><img src=\"http://sohoa.vnexpress.net/Files/Subject/3B/9A/FC/12/5.jpg\" border=\"1\" alt=\"\" width=\"400\" height=\"345\" align=\"center\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\"><img src=\"http://sohoa.vnexpress.net/Files/Subject/3B/9A/FC/12/6.jpg\" border=\"1\" alt=\"\" width=\"400\" height=\"469\" align=\"center\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\"><img src=\"http://sohoa.vnexpress.net/Files/Subject/3B/9A/FC/12/7.jpg\" border=\"1\" alt=\"\" width=\"400\" height=\"375\" align=\"center\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>', 331, 200, NULL, 248, 1, 0),
(627, 45, 1, 'Dell Flash ', '- Hệ điều hành Android Froyo\r\n- Máy ảnh 5.0 MP, hỗ trợ lấy nét tự động\r\n- Mạng 3G với HSDPA 14.4 Mbps; HSUPA 5.76 Mbps\r\n- Bluetooth 3.0 và Wifi 802.11 b/g/i, DLNA & UPnP\r\n- Google Search, Maps, Gmail\r\n- Công nghệ lọc tiếng ồn', '0000-00-00', 7894000, 7894000, 'Dell_Flash_b.jpg', '<div class=\"newstitle\">\r\n<h1>Lộ diện c&aacute;c mẫu điện thoại mới của DELL</h1>\r\n</div>\r\n<div class=\"newsintro\">\r\n<p>Tin vui đối với những người sử dụng Smart Phone, Dell đ&atilde; trở lại với h&agrave;ng loạt sản phẩm \'khủng\' v&agrave; sẽ c&oacute; một cuộc cạnh tranh s&ocirc;i nổi tr&ecirc;n thị trường điện thoại.</p>\r\n</div>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><strong>Dell Lightning chạy Windows Phone 7<br /></strong></span></p>\r\n<p style=\"text-align: justify;\">Dell Lightning c&oacute; m&agrave;n h&igrave;nh cảm ứng đa điểm OLED lớn 4.1-inch với 3 ph&iacute;m cảm ứng ph&iacute;a dưới theo ti&ecirc;u chẩn m&agrave; Microsoft đặt ra, k&egrave;m theo đ&oacute; l&agrave; một b&agrave;n ph&iacute;m trượt QWERTY. Vẫn chưa c&oacute; th&ocirc;ng tin ch&iacute;nh thức từ Dell về ng&agrave;y ra mắt, c&oacute; lẽ bạn sẽ phải đợi đến năm 2011 v&agrave; tất nhi&ecirc;n l&agrave; sau khi Windows Phone 7 được ph&aacute;t h&agrave;nh.</p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Lightning-1.jpg\" alt=\"\" width=\"580\" height=\"312\" /></p>\r\n<p style=\"text-align: justify;\">Dell Lightning c&oacute; cấu h&igrave;nh kh&aacute; tốt với bộ xử l&yacute; QSD8250 1GHz Snapdragon, 1Gb flash ROM v&agrave; 512Mb RAM, khe cắm microSD với thẻ 8GB t&iacute;ch hợp sẵn. M&aacute;y sử dụng m&agrave;n h&igrave;nh OLED 4.1\" của samsung, camera 5.0 megapixel. Tất nhi&ecirc;n, cũng kh&ocirc;ng thể thiếu c&aacute;c t&iacute;nh năng kh&aacute;c như GPS, la b&agrave;n số, FM radio ...</p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Lightning-2.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Lightning-3.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Lightning-4.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Lightning-5.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-size: small;\">Dell Flash sẽ chạy Android Froyo</span></strong></p>\r\n<p style=\"text-align: justify;\">Đầu ti&ecirc;n phải n&oacute;i đến, Flash ở đ&acirc;y kh&ocirc;ng li&ecirc;n quan g&igrave; với Adobe m&agrave; đ&acirc;y l&agrave; một sản phẩm mới của Dell được giới thiệu lần n&agrave;y. Dell Flash sẽ chạy hệ điều h&agrave;nh mới nhất của Android, đ&oacute; l&agrave; Froyo. Hướng tới thời trang v&agrave; s&agrave;nh điệu, Dell Flash chỉ d&agrave;y c&oacute; 11mm với m&agrave;n h&igrave;nh lớn 3.5-inch. Bộ nh&acirc;n b&ecirc;n trong l&agrave; Qualcomm MSM7230, ch&iacute;nh v&igrave; thế Flash chạy tr&ecirc;n cả 2 bằng tần GSM v&agrave; CDMA.</p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Flash-1.jpg\" alt=\"\" width=\"580\" height=\"749\" /></p>\r\n<p style=\"text-align: justify;\">Dell Flash sử dụng bộ xử l&yacute; Qualcomm MSM7230 tốc độ 800MHz với 512Mb RAM v&agrave; 512Mb ROM, hỗ trợ thẻ nhớ microSD. M&aacute;y c&oacute; WiFi, Bluetooth 3.0, AGPS, TV-out, giắc cắm tai nghe 3.5mm. Một điều đ&aacute;ng tiếc l&agrave; Flash kh&ocirc;ng d&ugrave;ng m&agrave;n h&igrave;nh OLED m&agrave; thay v&agrave;o đ&oacute; l&agrave; m&agrave;n h&igrave;nh TFT 262KColor, 24bit m&agrave;u. M&aacute;y c&oacute; camera 5.0 megapixel với đ&egrave;n LED flash, zoom 8x với khả năng quay video. Pin k&egrave;m theo m&aacute;y c&oacute; dung lượng 1300mAh.</p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Flash-2.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Flash-3.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Flash-4.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Flash-5.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: left;\"><span style=\"font-size: small;\"><strong>Dell Thunder, điện thoại Android 2.1 với m&agrave;n h&igrave;nh OLED</strong></span></p>\r\n<p style=\"text-align: justify;\">Kh&aacute;c với Lightning hướng tới đối tượng l&agrave; những nh&agrave; kinh doanh, những con người của c&ocirc;ng việc, Dell Thunder x&aacute;c định đối tượng của m&igrave;nh l&agrave; những người th&iacute;ch một chiếc điện thoại để giải tr&iacute; hơn l&agrave; để l&agrave;m việc. Chạy Android 2.1 c&ugrave;ng với Flash 10.1, Dell Thunder gi&uacute;p bạn c&oacute; thể chia sẻ th&ocirc;ng tin mọi l&uacute;c mọi nơi với facebook v&agrave; twitter, giải tr&iacute; th&ocirc;ng qua Hulu v&agrave; Youtube t&iacute;ch hợp sẵn.</p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Thunder-1.jpg\" alt=\"\" width=\"580\" height=\"855\" /></p>\r\n<p style=\"text-align: justify;\">Dell mang đến cho Thunder một giao diện ho&agrave;n to&agrave;n mới, bắt mắt hơn với t&ecirc;n gọi Stage UI \"STAGE MAKES IT YOURS| What Matters to You\", c&ugrave;ng với m&agrave;n h&igrave;nh OLED lớn 4.1-inch. Kh&ocirc;ng c&oacute; th&ocirc;ng tin chi tiết về cấu h&igrave;nh của Thunder, c&oacute; lẽ cấu h&igrave;nh m&aacute;y cũng sẽ tương đương Lightning với chip Snapdragon 1GHz. Một điều chắc chắn l&agrave; Thunder chạy Android 2.1 + flash 10.1 v&agrave; c&oacute; camera 8.0 megapixel. Ngo&agrave;i ra m&aacute;y cũng c&oacute; t&iacute;ch hợp Exchange Activesync để đồng bộ dữ liệu, hỗ trợ kết nối 3G.</p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Thunder-2.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Thunder-3.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Thunder-4.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Thunder-5.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: left;\"><span style=\"font-size: small;\"><strong>Dell Smoke, điện thoại Android Froyo với b&agrave;n ph&iacute;m QWERTY </strong></span></p>\r\n<p style=\"text-align: justify;\">Khởi đầu với tia chớp, sấm s&eacute;t v&agrave; kết th&uacute;c l&agrave; kh&oacute;i tung m&ugrave; trời, Dell quả thực đ&atilde; khuấy động thị trường điện thoại Android với loại sản phẩm mới của m&igrave;nh. Dell Smoke dạng thanh với m&agrave;n h&igrave;nh lớn 2.8-inch độ ph&acirc;n giải QVGA v&agrave; một b&agrave;n ph&iacute;m QWERTY, rất th&iacute;ch hợp với ai th&iacute;ch nhắn tin v&agrave; phải l&agrave;m việc nhiều. Dell Smoke c&oacute; kiểu d&aacute;ng truyền thống m&agrave; nhiều người y&ecirc;u th&iacute;ch, giống như với Palm Treo v&agrave; Blackberry.</p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Smoke-1.jpg\" alt=\"\" width=\"355\" height=\"500\" /></p>\r\n<p style=\"text-align: justify;\">Dell Smoke sử dụng bộ xử l&yacute; Qualcomm MSM7230 tốc độ 800MHz, chạy tr&ecirc;n cả hai mạng GSM v&agrave; CDMA, với 512Mb RAM v&agrave; 256Mb ROM, hỗ trợ thẻ nhớ microSD. M&aacute;y c&oacute; WiFi, Bluetooth 2.1, AGPS, giắc cắm tai nghe 3.5mm. Smoke c&oacute; m&agrave;n h&igrave;nh cảm ứng TFT (kh&ocirc;ng đa điểm?) k&iacute;ch thước 2.8-inch với độ ph&acirc;n giải chỉ c&oacute; 240 x 320 (QVGA). M&aacute;y cũng c&oacute; camera 5.0 megapixel tự động lấy n&eacute;t. Pin theo m&aacute;y c&oacute; dung lượng 1170 mAh.</p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Smoke-2.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Smoke-3.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Smoke-4.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Smoke-5.jpg\" alt=\"\" /></p>', 416, 200, NULL, 360, 1, 1),
(628, 41, 1, 'HTC DIAMOND2', 'Màn hình TFT, 65.536 màu, rộng 3.2 inches\r\nMáy ảnh 5.0 MP (2592 x 1944 pixels)\r\nĐài FM tích hợp: Có\r\nNghe nhạc: MP3, WAV, AAC, WMA, AAC+\r\nXem Phim: 3GP, WMV, MP4\r\nKết nối 3G: HSDPA 7.2 Mbps, Wifi: Wi-Fi 802.11 b/g, GPS: A-GPS, Bluetooth: Có\r\nỨng dụng văn ', '0000-00-00', 10999000, 10999000, 'HTC Diamond 2_1.jpg', '<p class=\"Title\">Hỏi về gi&aacute; chuẩn của HTC Touch Diamond2</p>\r\n<p class=\"Lead\">Tại Pico Plaza l&agrave; 12 triệu, tại Nhật Cường mobile l&agrave; 8,5 triệu. (Nguyen Nam)</p>\r\n<p class=\"Normal\"><span><em>T&ecirc;n người gửi: Nguyen Nam<br />Email: benbanbia@...</em> </span></p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://sohoa.vnexpress.net/Files/Subject/3B/9B/13/FB/htc_touch_diamond2_handsonm.jpg\" border=\"1\" alt=\"HTC Touch Diamond2. Ảnh: Engadget.\" width=\"480\" height=\"300\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">HTC Touch Diamond2. Ảnh: <em>Engadget.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\"><span>T&ocirc;i định mua HTC Touch Diamond2 v&agrave; đ&atilde; tham khảo gi&aacute; tại c&aacute;c cửa h&agrave;ng ĐTDĐ tại H&agrave; Nội (đều l&agrave; những nới b&aacute;n c&oacute; uy t&iacute;n) v&agrave; thấy gi&aacute; b&aacute;n rất kh&aacute;c nhau. </span></p>\r\n<p class=\"Normal\"><span>Tại Pico Plaza l&agrave; 12 triệu, tại Nhật Cường mobile l&agrave; 8,5 triệu. T&ocirc;i rất thắc mắc về điều n&agrave;y v&agrave; chưa biết n&ecirc;n quyết định thế n&agrave;o cho đ&uacute;ng. T&ocirc;i nghĩ sẽ nhận được c&acirc;u trả lời ch&iacute;nh x&aacute;c v&agrave; kh&aacute;ch quan nhất tại Cộng đồng Số ho&aacute;. </span></p>\r\n<p class=\"Normal\"><span>Mong c&aacute;c bạn giải th&iacute;ch v&agrave; tư vấn gi&uacute;p t&ocirc;i nh&eacute;. Xin tr&acirc;n th&agrave;nh cảm ơn! </span></p>', 88, 200, NULL, 57, 1, 0),
(629, 41, 1, 'HTC TATTOO', 'Hệ điều hành Android OS, v1.5\r\n256 MB RAM, 512 MB ROM\r\nCPU: Qualcomm MSM 7225, 528 MHz processor\r\nCamera 3.2 MP, hỗ trợ lấy nét tự động\r\nMạng 3G với tốc độ truyền dữ liệu 7.2 Mbps\r\nKết nối Wifi, Bluetooth 2.0\r\nChức năng định vị GPS\r\nĐài FM', '0000-00-00', 7999000, 7999000, 'HTC_Tatoo_silver.jpg', '<p class=\"Title\">HTC Tattoo ch&iacute;nh h&atilde;ng gi&aacute; kh&ocirc;ng thấp</p>\r\n<p class=\"Lead\">Mức gi&aacute; của Tattoo kh&ocirc;ng b&igrave;nh d&acirc;n như trước đ&oacute;, với 8,5 triệu đồng, chiếc Android thứ ba của HTC tại Việt Nam nằm trong ph&acirc;n kh&uacute;c h&agrave;ng cao cấp.<br />&gt; <span style=\"text-decoration: underline;\"><a href=\"http://sohoa.vnexpress.net/SH/Dien-thoai/PDA/2009/09/3B9B0BFD/\">HTC Tattoo - chiếc Android gi&aacute; thấp</a></span></p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://sohoa.vnexpress.net/Files/Subject/3B/9B/10/87/HTC_Tattoo.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"282\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Tattoo tại Việt Nam nằm trong ph&acirc;n kh&uacute;c cao cấp.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">H&ocirc;m nay, HTC Tattoo đ&atilde; xuất hiện tr&ecirc;n một số cửa h&agrave;ng, si&ecirc;u thị di động ở H&agrave; Nội v&agrave; TP HCM. Model được HTC r&uacute;t gọn nhiều trang bị, v&agrave; nhắm tới người d&ugrave;ng c&oacute; t&uacute;i tiền eo hẹp, muốn sử dụng một chiếc di động Android nhiều kết nối. Tattoo được xem l&agrave; model sở hữu lợi thế về gi&aacute;, tham gia cuộc đua smartphone gi&aacute; rẻ rầm rộ hiện nay.</p>\r\n<p class=\"Normal\">Tuy nhi&ecirc;n, mức gi&aacute; 8,5 triệu đồng của model n&agrave;y cho thị trường Việt Nam cao hơn c&aacute;c model kh&aacute;c như Nokia E75, E71... v&agrave; nằm trong ph&acirc;n kh&uacute;c smartphone cao cấp, mặc d&ugrave; kh&ocirc;ng so k&egrave; với nhiều model tr&ecirc;n 10 triệu đồng. Hiện HTC đ&atilde; giới thiệu ba chiếc Android cho thị trường Việt Nam, c&ugrave;ng với Hero (12,5 triệu đồng), Magic (11,4 triệu đồng).</p>\r\n<p class=\"Normal\">So với hai phi&ecirc;n bản đ&agrave;n anh, Tattoo được r&uacute;t gọn nhiều trang bị, m&aacute;y c&oacute; m&agrave;n h&igrave;nh nhỏ, d&ugrave; vẫn hỗ trợ c&aacute;c kết nối HSDPA, Wi-Fi, GPS. Ngo&agrave;i ra, model n&agrave;y cũng c&agrave;i đặt sẵn giao diện Sense như \"&ocirc;ng anh\" Hero.</p>\r\n<p class=\"Normal\">M&agrave;n h&igrave;nh cảm ứng của m&aacute;y rộng 2,8 inch, chuẩn QVGA với độ ph&acirc;n giải 240 x 320 pixel, camera 3,2 Megapixel. C&ocirc;ng nghệ cảm ứng của Tattoo l&agrave; điện trở, m&aacute;y trang bị b&agrave;n ph&iacute;m ảo, nhiều phần mềm kết nối mạng x&atilde; hội đi k&egrave;m.</p>', 291, 200, NULL, 205, 1, 0),
(630, 45, 1, 'DELL SMOKE', '- Hệ điều hành Android Froyo\r\n- Máy ảnh 5.0 MP, hỗ trợ lấy nét tự động\r\n- Mạng 3G với HSDPA 14.4 Mbps; HSUPA 5.76 Mbps\r\n- Bluetooth 2.1 và Wifi 802.11\r\n- Google Search, Maps, Gmail\r\n- Công nghệ lọc tiếng ồn', '0000-00-00', 6745000, 6745000, 'Dell_Smoke_b.jpg', '<div id=\"tabs_2\" class=\"ui-tabs-panel ui-widget-content ui-corner-bottom\"></div>\r\n<div class=\"ui-tabs-panel ui-widget-content ui-corner-bottom\">\r\n<p style=\"text-align: center;\"><a class=\"highslide\" onclick=\"return hs.expand(this)\" href=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Smoke_1.jpg\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Smoke_1.jpg\" alt=\"\" width=\"580\" height=\"700\" /></a></p>\r\n<div class=\"newstitle\">\r\n<h1>Lộ diện c&aacute;c mẫu điện thoại mới của DELL</h1>\r\n</div>\r\n<div class=\"newsintro\">\r\n<p>Tin vui đối với những người sử dụng Smart Phone, Dell đ&atilde; trở lại với h&agrave;ng loạt sản phẩm \'khủng\' v&agrave; sẽ c&oacute; một cuộc cạnh tranh s&ocirc;i nổi tr&ecirc;n thị trường điện thoại.</p>\r\n</div>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><strong>Dell Lightning chạy Windows Phone 7<br /></strong></span></p>\r\n<p style=\"text-align: justify;\">Dell Lightning c&oacute; m&agrave;n h&igrave;nh cảm ứng đa điểm OLED lớn 4.1-inch với 3 ph&iacute;m cảm ứng ph&iacute;a dưới theo ti&ecirc;u chẩn m&agrave; Microsoft đặt ra, k&egrave;m theo đ&oacute; l&agrave; một b&agrave;n ph&iacute;m trượt QWERTY. Vẫn chưa c&oacute; th&ocirc;ng tin ch&iacute;nh thức từ Dell về ng&agrave;y ra mắt, c&oacute; lẽ bạn sẽ phải đợi đến năm 2011 v&agrave; tất nhi&ecirc;n l&agrave; sau khi Windows Phone 7 được ph&aacute;t h&agrave;nh.</p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Lightning-1.jpg\" alt=\"\" width=\"580\" height=\"312\" /></p>\r\n<p style=\"text-align: justify;\">Dell Lightning c&oacute; cấu h&igrave;nh kh&aacute; tốt với bộ xử l&yacute; QSD8250 1GHz Snapdragon, 1Gb flash ROM v&agrave; 512Mb RAM, khe cắm microSD với thẻ 8GB t&iacute;ch hợp sẵn. M&aacute;y sử dụng m&agrave;n h&igrave;nh OLED 4.1\" của samsung, camera 5.0 megapixel. Tất nhi&ecirc;n, cũng kh&ocirc;ng thể thiếu c&aacute;c t&iacute;nh năng kh&aacute;c như GPS, la b&agrave;n số, FM radio ...</p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Lightning-2.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Lightning-3.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Lightning-4.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Lightning-5.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-size: small;\">Dell Flash sẽ chạy Android Froyo</span></strong></p>\r\n<p style=\"text-align: justify;\">Đầu ti&ecirc;n phải n&oacute;i đến, Flash ở đ&acirc;y kh&ocirc;ng li&ecirc;n quan g&igrave; với Adobe m&agrave; đ&acirc;y l&agrave; một sản phẩm mới của Dell được giới thiệu lần n&agrave;y. Dell Flash sẽ chạy hệ điều h&agrave;nh mới nhất của Android, đ&oacute; l&agrave; Froyo. Hướng tới thời trang v&agrave; s&agrave;nh điệu, Dell Flash chỉ d&agrave;y c&oacute; 11mm với m&agrave;n h&igrave;nh lớn 3.5-inch. Bộ nh&acirc;n b&ecirc;n trong l&agrave; Qualcomm MSM7230, ch&iacute;nh v&igrave; thế Flash chạy tr&ecirc;n cả 2 bằng tần GSM v&agrave; CDMA.</p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Flash-1.jpg\" alt=\"\" width=\"580\" height=\"749\" /></p>\r\n<p style=\"text-align: justify;\">Dell Flash sử dụng bộ xử l&yacute; Qualcomm MSM7230 tốc độ 800MHz với 512Mb RAM v&agrave; 512Mb ROM, hỗ trợ thẻ nhớ microSD. M&aacute;y c&oacute; WiFi, Bluetooth 3.0, AGPS, TV-out, giắc cắm tai nghe 3.5mm. Một điều đ&aacute;ng tiếc l&agrave; Flash kh&ocirc;ng d&ugrave;ng m&agrave;n h&igrave;nh OLED m&agrave; thay v&agrave;o đ&oacute; l&agrave; m&agrave;n h&igrave;nh TFT 262KColor, 24bit m&agrave;u. M&aacute;y c&oacute; camera 5.0 megapixel với đ&egrave;n LED flash, zoom 8x với khả năng quay video. Pin k&egrave;m theo m&aacute;y c&oacute; dung lượng 1300mAh.</p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Flash-2.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Flash-3.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Flash-4.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Flash-5.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: left;\"><span style=\"font-size: small;\"><strong>Dell Thunder, điện thoại Android 2.1 với m&agrave;n h&igrave;nh OLED</strong></span></p>\r\n<p style=\"text-align: justify;\">Kh&aacute;c với Lightning hướng tới đối tượng l&agrave; những nh&agrave; kinh doanh, những con người của c&ocirc;ng việc, Dell Thunder x&aacute;c định đối tượng của m&igrave;nh l&agrave; những người th&iacute;ch một chiếc điện thoại để giải tr&iacute; hơn l&agrave; để l&agrave;m việc. Chạy Android 2.1 c&ugrave;ng với Flash 10.1, Dell Thunder gi&uacute;p bạn c&oacute; thể chia sẻ th&ocirc;ng tin mọi l&uacute;c mọi nơi với facebook v&agrave; twitter, giải tr&iacute; th&ocirc;ng qua Hulu v&agrave; Youtube t&iacute;ch hợp sẵn.</p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Thunder-1.jpg\" alt=\"\" width=\"580\" height=\"855\" /></p>\r\n<p style=\"text-align: justify;\">Dell mang đến cho Thunder một giao diện ho&agrave;n to&agrave;n mới, bắt mắt hơn với t&ecirc;n gọi Stage UI \"STAGE MAKES IT YOURS| What Matters to You\", c&ugrave;ng với m&agrave;n h&igrave;nh OLED lớn 4.1-inch. Kh&ocirc;ng c&oacute; th&ocirc;ng tin chi tiết về cấu h&igrave;nh của Thunder, c&oacute; lẽ cấu h&igrave;nh m&aacute;y cũng sẽ tương đương Lightning với chip Snapdragon 1GHz. Một điều chắc chắn l&agrave; Thunder chạy Android 2.1 + flash 10.1 v&agrave; c&oacute; camera 8.0 megapixel. Ngo&agrave;i ra m&aacute;y cũng c&oacute; t&iacute;ch hợp Exchange Activesync để đồng bộ dữ liệu, hỗ trợ kết nối 3G.</p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Thunder-2.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Thunder-3.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Thunder-4.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Thunder-5.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: left;\"><span style=\"font-size: small;\"><strong>Dell Smoke, điện thoại Android Froyo với b&agrave;n ph&iacute;m QWERTY </strong></span></p>\r\n<p style=\"text-align: justify;\">Khởi đầu với tia chớp, sấm s&eacute;t v&agrave; kết th&uacute;c l&agrave; kh&oacute;i tung m&ugrave; trời, Dell quả thực đ&atilde; khuấy động thị trường điện thoại Android với loại sản phẩm mới của m&igrave;nh. Dell Smoke dạng thanh với m&agrave;n h&igrave;nh lớn 2.8-inch độ ph&acirc;n giải QVGA v&agrave; một b&agrave;n ph&iacute;m QWERTY, rất th&iacute;ch hợp với ai th&iacute;ch nhắn tin v&agrave; phải l&agrave;m việc nhiều. Dell Smoke c&oacute; kiểu d&aacute;ng truyền thống m&agrave; nhiều người y&ecirc;u th&iacute;ch, giống như với Palm Treo v&agrave; Blackberry.</p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Smoke-1.jpg\" alt=\"\" width=\"355\" height=\"500\" /></p>\r\n<p style=\"text-align: justify;\">Dell Smoke sử dụng bộ xử l&yacute; Qualcomm MSM7230 tốc độ 800MHz, chạy tr&ecirc;n cả hai mạng GSM v&agrave; CDMA, với 512Mb RAM v&agrave; 256Mb ROM, hỗ trợ thẻ nhớ microSD. M&aacute;y c&oacute; WiFi, Bluetooth 2.1, AGPS, giắc cắm tai nghe 3.5mm. Smoke c&oacute; m&agrave;n h&igrave;nh cảm ứng TFT (kh&ocirc;ng đa điểm?) k&iacute;ch thước 2.8-inch với độ ph&acirc;n giải chỉ c&oacute; 240 x 320 (QVGA). M&aacute;y cũng c&oacute; camera 5.0 megapixel tự động lấy n&eacute;t. Pin theo m&aacute;y c&oacute; dung lượng 1170 mAh.</p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Smoke-2.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Smoke-3.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Smoke-4.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img style=\"border: 1px solid black;\" src=\"http://www.mainguyen.vn/img/inposts/1004/Dell-Smoke-5.jpg\" alt=\"\" /></p>\r\n</div>', 393, 200, NULL, 354, 1, 0),
(631, 41, 1, 'HTC SNAP', 'Thiết kế với bàn phím QWERTY đầy đủ\r\nHệ điều hành Windows Mobile 6.1\r\nCPU: 528 MHz processor\r\nROM 256 MB, RAM 192 MB\r\nKết nối 3G với tốc độ download lên đến 7.2 Mbps\r\nCamera 2.0 MP, hỗ trợ quay phim\r\nChức năng định vị toàn cầu GPS\r\nKết nối Wifi, Bluetooth', '0000-00-00', 6999000, 6999000, 'HTC Snap_1.jpg', '<p class=\"Title\">HTC Snap, \'anh em\' của BlackBerry</p>\r\n<p class=\"Lead\">G&acirc;y ấn tượng bằng b&agrave;n ph&iacute;m v&agrave; phần mềm lưu danh bạ nhanh, nhưng Snap bị ch&ecirc; v&igrave; pin yếu, giao diện Windows Mobile điều khiển chậm.<br />&gt; <span style=\"text-decoration: underline;\"><a href=\"http://sohoa.vnexpress.net/SH/Dien-thoai/Smartphone/2009/06/3B9B053A/\">\'Đập hộp\' HTC Snap</a></span> / <span style=\"text-decoration: underline;\"><a href=\"http://sohoa.vnexpress.net/SH/Dien-thoai/Smartphone/2009/04/3B9AFE2D/\">Cận cảnh Snap</a></span> / <span style=\"text-decoration: underline;\"><a href=\"http://sohoa.vnexpress.net/SH/Dien-thoai/Smartphone/2009/04/3B9AFE1D/\">Snap đối thủ của Nokia E71</a></span></p>\r\n<p class=\"Normal\">Dễ d&agrave;ng nhận biết, HTC Snap được nh&agrave; sản xuất nhắm tới người d&ugrave;ng thường xuy&ecirc;n soạn thảo, giống như c&aacute;c thiết bị BlackBerry hay Nokia E71.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://sohoa.vnexpress.net/Files/Subject/3B/9B/09/79/e71.jpg\" border=\"1\" alt=\"Snap b&ecirc;n cạnh Nokia E71. Ảnh: Cnet.\" width=\"480\" height=\"312\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Snap b&ecirc;n cạnh Nokia E71. Ảnh: <em>Cnet.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\"><strong><span style=\"color: #2f2f2f;\">Snap gần như bắt chước d&ograve;ng BlackBerry Curve từ thiết kế đến c&aacute;c lớp điều khiển</span></strong>. Tuy nhi&ecirc;n, so s&aacute;nh về k&iacute;ch thước, m&aacute;y lớn hơn, m&agrave;n h&igrave;nh của model n&agrave;y rộng 2,4 inch.</p>\r\n<p class=\"Normal\">Ngay b&ecirc;n dưới m&agrave;n h&igrave;nh, một d&atilde;y c&aacute;c n&uacute;t bấm, trong đ&oacute; vi&ecirc;n bi như trackball tr&ecirc;n BlackBerry nằm ở vị tr&iacute; trung t&acirc;m. Model n&agrave;y sở hữu b&agrave;n ph&iacute;m QWERTY đầy đủ.</p>\r\n<p class=\"Normal\">Mặc d&ugrave; m&agrave;n h&igrave;nh của Snap tr&ocirc;ng kh&aacute; n&eacute;t v&agrave; s&aacute;ng, tuy nhi&ecirc;n độ ph&acirc;n giải lại chỉ 320 x 240 pixel, thua xa chiếc BlackBerry Curve 8900, model n&agrave;y c&oacute; độ ph&acirc;n giải 480 x 360 pixel. Đ&acirc;y l&agrave; trang bị c&oacute; &yacute; nghĩa lớn khi duyệt web, bởi Snap sẽ kh&ocirc;ng cho c&aacute;c k&yacute; tự v&agrave; h&igrave;nh ảnh hiện thị tốt bằng, ngo&agrave;i ra trang web cũng trở n&ecirc;n kh&oacute; nh&igrave;n hơn.</p>\r\n<p class=\"Normal\">Tuy nhi&ecirc;n, thất vọng m&agrave;n h&igrave;nh chỉ l&agrave; một điểm rất nhỏ, b&agrave;n ph&iacute;m thực của Snap tuyệt vời, lấy lại h&igrave;nh ảnh cho model. C&aacute;c ph&iacute;m bấm của m&aacute;y nằm ri&ecirc;ng rẽ, c&aacute;ch nhau theo h&agrave;ng, nh&ocirc; cao b&ecirc;n tr&ecirc;n v&agrave; rộng, cho ph&eacute;p đưa ng&oacute;n tay, chạm v&agrave;o dễ d&agrave;ng để soạn thảo e-mail, tin nhắn tốc độ nhanh.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://sohoa.vnexpress.net/Files/Subject/3B/9B/09/79/ban-phim.jpg\" border=\"1\" alt=\"B&agrave;n ph&iacute;m của Snap dễ sử dụng. Ảnh: Cnet.\" width=\"480\" height=\"360\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">B&agrave;n ph&iacute;m của Snap dễ sử dụng. Ảnh: Cnet.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\"><span style=\"color: #2f2f2f;\"><strong>Snap chạy tr&ecirc;n hệ điều h&agrave;nh Windows Mobile 6.1 Standard - đ&acirc;y l&agrave; phi&ecirc;n bản d&agrave;nh cho c&aacute;c mẫu smartphone m&agrave;n h&igrave;nh cứng</strong>.</span> Mặc d&ugrave; việc di chuyển c&aacute;c Menu, đi v&agrave;o ứng dụng kh&aacute; dễ d&agrave;ng, tuy nhi&ecirc;n việc t&ugrave;y chọn, sử trụng trackball của m&aacute;y kh&ocirc;ng tốt bằng m&agrave;n h&igrave;nh cảm ứng. V&iacute; dụ, khi đi đến c&aacute;c ứng dụng, người d&ugrave;ng sẽ mất thời gian lăn qua c&aacute;c trang, kh&aacute;c với m&agrave;n h&igrave;nh chạm dễ d&agrave;ng vuốt nhẹ để đi thẳng đến ứng dụng.</p>\r\n<p class=\"Normal\">Trong phi&ecirc;n bản n&agrave;y, HTC trang bị phần mềm c&oacute; t&ecirc;n l&agrave; Inner Circle, cho ph&eacute;p đi v&agrave;o th&ocirc;ng qua c&aacute;c ph&iacute;m bấm chuy&ecirc;n dụng. Đ&acirc;y l&agrave; ứng dụng hỗ trợ đường v&agrave;o c&aacute;c chương tr&igrave;nh nhanh hơn như kiếm tra tin nhắn đến, c&aacute;c danh bạ quan trọng. Việc th&ecirc;m danh bạ th&ocirc;ng qua phần mềm tr&ecirc;n trở n&ecirc;n tiện dụng hơn từ e-mail, tin nhắn bằng việc lọc c&aacute;c k&yacute; tự, chữ k&yacute;...</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://sohoa.vnexpress.net/Files/Subject/3B/9B/09/79/handon.jpg\" border=\"1\" alt=\"M&aacute;y c&oacute; tốc độ xử l&yacute; nhanh. Ảnh: Cnet.\" width=\"480\" height=\"360\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">M&aacute;y c&oacute; tốc độ xử l&yacute; nhanh. Ảnh: <em>Cnet.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\"><span style=\"color: #2f2f2f;\"><strong>Snap l&agrave; chiếc smartphone c&oacute; khả năng xử l&yacute; nhanh</strong>.</span> Model n&agrave;y sử dụng bộ vi xử l&yacute; Qualcomm, tốc độ 528 MHz, RAM đi theo m&aacute;y c&oacute; dung lượng 192 MB, đủ bộ nhớ để chạy c&aacute;c ứng dụng, trong khi ROM lại chỉ 256 MB, qu&aacute; &iacute;t để lưu c&aacute;c file văn bản, người d&ugrave;ng c&oacute; thể chọn giải ph&aacute;p lưu tr&ecirc;n thẻ nhớ microSD.</p>\r\n<p class=\"Normal\">Đ&acirc;y cũng l&agrave; chiếc smartphone đa dạng về kết nối, từ HSDPA, Wi-Fi để duyệt web, ngo&agrave;i ra m&aacute;y c&ograve;n c&oacute; Bluetooth hỗ trợ đ&agrave;m thoại với tai nghe kh&ocirc;ng d&acirc;y. Snap chạy tr&ecirc;n 4 băng tần, cho ph&eacute;p sử dụng được tại tất cả c&aacute;c quốc gia tr&ecirc;n thế giới, hỗ trợ GPS, l&agrave;m việc tốt với bản đồ Google Maps, x&aacute;c định vị tr&iacute;.</p>\r\n<p class=\"Normal\">Camera 2 Megapixel của m&aacute;y l&agrave;m việc ở mức cơ bản, Snap thiếu t&iacute;nh năng tự động lấy n&eacute;t v&agrave; đ&egrave;n flash, do vậy c&aacute;c bức h&igrave;nh chụp trong điều kiện thiếu s&aacute;ng rất k&eacute;m. M&aacute;y được HTC hướng tới người d&ugrave;ng doanh nh&acirc;n, những kh&aacute;ch h&agrave;ng xem camera kh&ocirc;ng phải l&agrave; trang bị quan trọng.</p>\r\n<p class=\"Normal\">Một trong những điểm hạn chế l&agrave; thời gian sử dụng pin của m&aacute;y kh&ocirc;ng d&agrave;i. Trong khi hầu hết c&aacute;c đối thủ BlackBerry cho ph&eacute;p người d&ugrave;ng sử dụng c&aacute;c chức năng lướt web, gọi điện v&agrave; d&ugrave;ng b&igrave;nh thường được v&agrave;i ng&agrave;y th&igrave; Snap chỉ hơn k&eacute;m một ng&agrave;y.</p>', 190, 200, NULL, 154, 1, 0),
(632, 45, 1, 'DELL STREAK 7', 'Hệ điều hành Android 2.2 Froyo, Flash 10.1\r\nMàn hình rộng 7.0 inches\r\nMáy ảnh 5.0 MP (2592 x 1944 pixels, máy ảnh phụ 1.3 MP\r\nNghe nhạc: AAC, AAC+, AAC++, MP3, WMA, eAAC+\r\nXem Phim: 3GP, H.263, H.264(MPEG4-AVC), MP4, WMV\r\nKết nối 3G, Wi-Fi 802.11 b/g/n, A', '0000-00-00', 6741000, 6538770, 'dell_streak_7_b.jpg', '<h2 style=\"margin: 0px; padding: 5px 0px 3px 10px; color: #ff9501; font-weight: bolder;\">Dell Streak 7 lộ diện</h2>\r\n<div class=\"news_description\" style=\"padding: 0px 0px 0px 10px; color: #606060; margin-bottom: 10px;\">Những h&igrave;nh ảnh mới nhất của Dell Streak 7 cho thấy m&aacute;y c&oacute; kiểu d&aacute;ng giống so với model m&agrave;n h&igrave;nh 5 inch v&agrave; k&iacute;ch thước m&agrave;n h&igrave;nh tương đương với Galaxy Tab.</div>\r\n<div class=\"new_pro_detail_contet\" style=\"padding: 5px 3px 5px 10px;\">\r\n<table style=\"border-collapse: collapse; background-color: #ffffff;\" border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img title=\"Dell Streak 7 lộ diện\" src=\"http://images.thegioididong.com/Files/2010/12/24/26554/393_Dell-Streak-7-lo-dien.jpg\" border=\"1\" alt=\"Dell Streak 7 lộ diện\" width=\"500\" height=\"271\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Dell Streak v&agrave; slogan mới của Dell lộ diện qua một số bức ảnh quảng c&aacute;o. Ảnh:<span class=\"Apple-converted-space\">&nbsp;</span><em>Engadget.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"margin: 14px 0px; line-height: 18px;\">H&ocirc;m nay, trang tin<span class=\"Apple-converted-space\">&nbsp;</span><em>Engadget<span class=\"Apple-converted-space\">&nbsp;</span></em>c&ocirc;ng bố một số bức ảnh quảng c&aacute;o thương mại được cho l&agrave; của Dell Streak m&agrave;n h&igrave;nh 7 inch. Những h&igrave;nh ảnh n&agrave;y giới thiệu đầy đủ khả năng của m&aacute;y như tr&ograve; chơi, mạng x&atilde; hội, khả năng kết nối Internet, chương tr&igrave;nh đọc s&aacute;ch Blio, giao diện Stage UI tương tự như tr&ecirc;n Dell Streak 5.</p>\r\n<p class=\"Normal\" style=\"margin: 14px 0px; line-height: 18px;\">Ngo&agrave;i giới thiệu chiếc m&aacute;y t&iacute;nh bảng với t&ecirc;n gọi Dell Streak 7, Dell cũng cho thấy sẽ sử dụng một khẩu hiệu kh&aacute;c trong thời gian tới l&agrave; \"You can tell it\'s Dell\" (Bạn c&oacute; thể n&oacute;i đ&oacute; l&agrave; Dell).</p>\r\n<p class=\"Normal\" style=\"margin: 14px 0px; line-height: 18px;\">Cũng như model 5 inch, Dell Streak 7 sẽ chạy tr&ecirc;n hệ điều h&agrave;nh Android nhưng chưa r&otilde; phi&ecirc;n bản n&agrave;o. M&aacute;y nhiều khả năng sẽ được ra mắt tại triển l&atilde;m CES 2011.</p>\r\n<p class=\"Normal\" style=\"margin: 14px 0px; line-height: 18px;\">Hội chợ c&ocirc;ng nghệ CES 2011 (Consumer Electronics Show) diễn ra từ 6 đến 9/1/2011 tại Las Vegas, Mỹ.</p>\r\n<p class=\"Normal\" style=\"margin: 14px 0px; line-height: 18px;\"><strong><em><span style=\"text-decoration: underline;\">Ảnh quảng c&aacute;o bị lộ của Dell Streak 7:</span></em></strong></p>\r\n<table style=\"border-collapse: collapse; background-color: #ffffff;\" border=\"0\" cellspacing=\"2\" cellpadding=\"2\" width=\"77%\" align=\"center\" bgcolor=\"#ffffff\">\r\n<tbody>\r\n<tr>\r\n<td class=\"BoxLink\"><img title=\"Dell Streak 7 lộ diện\" src=\"http://images.thegioididong.com/Files/2010/12/24/26554/401_Dell-Streak-7-lo-dien.jpg\" border=\"1\" alt=\"Dell Streak 7 lộ diện\" width=\"500\" height=\"268\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\"><img title=\"Dell Streak 7 lộ diện\" src=\"http://images.thegioididong.com/Files/2010/12/24/26554/393_Dell-Streak-7-lo-dien.jpg\" border=\"1\" alt=\"Dell Streak 7 lộ diện\" width=\"500\" height=\"271\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\">H&igrave;nh ảnh Dell Streak 7 v&agrave; slogan mới của Dell.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\"><img title=\"Dell Streak 7 lộ diện\" src=\"http://images.thegioididong.com/Files/2010/12/24/26554/402_Dell-Streak-7-lo-dien.jpg\" border=\"1\" alt=\"Dell Streak 7 lộ diện\" width=\"500\" height=\"343\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\">Streak 7 sẽ chạy Android.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\"><img title=\"Dell Streak 7 lộ diện\" src=\"http://images.thegioididong.com/Files/2010/12/24/26554/403_Dell-Streak-7-lo-dien.jpg\" border=\"1\" alt=\"Dell Streak 7 lộ diện\" width=\"500\" height=\"278\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\">Model n&agrave;y c&oacute; giao diện Stage UI giống với bản 5 inch.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\"><img title=\"Dell Streak 7 lộ diện\" src=\"http://images.thegioididong.com/Files/2010/12/24/26554/404_Dell-Streak-7-lo-dien.jpg\" border=\"1\" alt=\"Dell Streak 7 lộ diện\" width=\"500\" height=\"323\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\"><img title=\"Dell Streak 7 lộ diện\" src=\"http://images.thegioididong.com/Files/2010/12/24/26554/405_Dell-Streak-7-lo-dien.jpg\" border=\"1\" alt=\"Dell Streak 7 lộ diện\" width=\"500\" height=\"293\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\">Với m&agrave;n h&igrave;nh 7 inch, Streak sẽ c&oacute; k&iacute;ch thước tương tự Galaxy Tab, m&aacute;y t&iacute;nh bảng của Samsung.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\"><img title=\"Dell Streak 7 lộ diện\" src=\"http://images.thegioididong.com/Files/2010/12/24/26554/406_Dell-Streak-7-lo-dien.jpg\" border=\"1\" alt=\"Dell Streak 7 lộ diện\" width=\"500\" height=\"281\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"BoxLink\">Streak 7 c&oacute; thể ra mắt tại triển l&atilde;m CES 2011 sắp tới.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>', 275, 200, NULL, 211, 1, 0);
INSERT INTO `dienthoai` (`idDT`, `idLoai`, `idCL`, `TenDT`, `MoTa`, `NgayCapNhat`, `Gia`, `GiaKM`, `urlHinh`, `baiviet`, `SoLanXem`, `SoLuongTonKho`, `GhiChu`, `SoLanMua`, `AnHien`, `Hot`) VALUES
(633, 38, 1, 'LG OPTIMUS ONE P500', 'Hệ điều hành Android 2.2\r\nMáy ảnh 3.0 MP, lấy né tự động\r\nMạng 3G với tốc độ truyền tari7.2Mbps\r\nKết nối mạng Wifi, GPS, Bluetooth\r\nĐài FM tích hợp', '0000-00-00', 5699000, 5699000, 'optic_one_b.jpg', '<h2 style=\"margin: 0px; padding: 5px 0px 3px 10px; color: #ff9501; font-weight: bolder;\">LG c&ocirc;ng bố d&ograve;ng di động Android Optimus</h2>\r\n<div class=\"news_description\" style=\"padding: 0px 0px 0px 10px; color: #606060; margin-bottom: 10px;\">H&ocirc;m qua, LG giới thiệu c&aacute;c mẫu di động mới trong d&ograve;ng Android gồm Optimus One v&agrave; Optimus Chic, những model sử dụng Android 2.2 Froyo.</div>\r\n<div class=\"new_pro_detail_contet\" style=\"padding: 5px 3px 5px 10px;\">\r\n<h1 class=\"Title\">&nbsp;</h1>\r\n<table style=\"border-collapse: collapse; background-color: #ffffff;\" border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img title=\"LG c&ocirc;ng bố d&ograve;ng di động Android Optimus\" src=\"http://images.thegioididong.com/Files/2010/07/06/13741/1_LG-cong-bo-dong-di-dong-Android-Optimus.jpg\" border=\"1\" alt=\"LG c&ocirc;ng bố d&ograve;ng di động Android Optimus\" width=\"480\" height=\"681\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Bộ sản phẩm nằm trong series Optimus.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"margin: 14px 0px; line-height: 18px;\">LG đồng thời cũng cho biết, họ sẽ ra mắt tới 10 model trong series Optimus (tiếng La tinh c&oacute; nghĩa l&agrave; nhất). Nếu như Optimus One v&agrave; Optimus Chic chạy sẵn Android 2.2, th&igrave; c&aacute;c model đ&atilde; c&oacute; của h&atilde;ng l&agrave; Optimus Q v&agrave; Z sẽ được n&acirc;ng cấp l&ecirc;n hệ điều h&agrave;nh mới trong v&agrave;i th&aacute;ng nữa.</p>\r\n<table style=\"border-collapse: collapse; background-color: #ffffff;\" border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img title=\"LG c&ocirc;ng bố d&ograve;ng di động Android Optimus\" src=\"http://images.thegioididong.com/Files/2010/07/06/13741/2_LG-cong-bo-dong-di-dong-Android-Optimus.jpg\" border=\"1\" alt=\"LG c&ocirc;ng bố d&ograve;ng di động Android Optimus\" width=\"480\" height=\"320\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Optimus One v&agrave; Optimus Chic chạy Android 2.2.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"margin: 14px 0px; line-height: 18px;\">Trong th&ocirc;ng c&aacute;o b&aacute;o ch&iacute; của m&igrave;nh, LG kh&ocirc;ng c&ocirc;ng bố nhiều chi tiết về Optimus One v&agrave; Chic ngo&agrave;i giới thiệu thiết kế m&agrave;n h&igrave;nh cảm ứng rộng v&agrave; sử dụng nền tảng mới. Đ&acirc;y được xem l&agrave; những chiếc smartphone tầm trung, hỗ trợ từ kết nối Wi-Fi trở l&ecirc;n.</p>\r\n<p class=\"Normal\" style=\"margin: 14px 0px; line-height: 18px;\">Cũng trong th&ocirc;ng b&aacute;o của m&igrave;nh, LG cho biết c&aacute;c thiết bị chạy Windows Phone 7 sẽ được h&atilde;ng giới thiệu ngay trong năm nay.</p>\r\n</div>', 106, 200, NULL, 92, 1, 1),
(634, 38, 1, 'LG BL40 NEW CHOCOLATE', 'Thiết kế thời trang với kiểu dáng Chocolate\r\nMàn hình cảm ứng rộng 4.0 inches,\r\nCamera 5 MP, camera phụ\r\nMáy nghe nhạc, xem phim nhiều định dạng\r\nChức năng bộ sưu ảnh 3D\r\nĐài FM, xem file văn bản\r\nKết nối 3G, GPS, Wifi, Bluetooth, Out-Tivi', '0000-00-00', 5479000, 5479000, 'LG_BL40_b.jpg', '<h1 class=\"Title\">Chocolate BL40 ch&iacute;nh h&atilde;ng tại Việt Nam</h1>\r\n<p class=\"Lead\">LG vừa ch&iacute;nh thức đưa chiếc di động \"ph&aacute; vỡ giới hạn về m&agrave;n h&igrave;nh\" New Chocolate BL40 về Việt Nam với gi&aacute; 7,9 triệu đồng.</p>\r\n<p class=\"Normal\">Hơi chậm hơn c&aacute;c thị trường kh&aacute;c, BL40 ch&iacute;nh h&atilde;ng được LG b&aacute;n ra sau Tết Nguy&ecirc;n đ&aacute;n, mức gi&aacute; 7,9 triệu đồng ngang bằng với c&aacute;c mẫu smartphone Nokia N86, E72.</p>\r\n<p class=\"Normal\">Đ&acirc;y kh&ocirc;ng phải l&agrave; chiếc di động th&ocirc;ng minh chạy hệ điều h&agrave;nh, BL40 nhấn mạnh v&agrave;o thiết kế, với kiểu d&aacute;ng như một thanh Chocolate, m&aacute;y c&oacute; c&aacute;c cạnh vu&ocirc;ng vắn, b&oacute;ng bẩy.</p>\r\n<p class=\"Normal\">Ấn tượng đặc biệt ở model n&agrave;y l&agrave; m&agrave;n h&igrave;nh 4 inch, tỷ lệ 21:9 rất đặc biệt, d&agrave;i, độ ph&acirc;n giải 800 x 345 pixel. LG trang bị c&ocirc;ng nghệ cảm ứng điện dung, cho ph&eacute;p d&ugrave;ng ng&oacute;n tay điều khiển, m&aacute;y c&oacute; giao diện S-class, được sửa đổi đ&ocirc;i ch&uacute;t ph&ugrave; hợp với kiểu m&agrave;n h&igrave;nh rộng về chiều dọc.</p>\r\n<p class=\"Normal\">Mặt sau m&aacute;y l&agrave; camera 5 Megapixel, hỗ trợ nhiều chế độ chụp h&igrave;nh như nhận diện khu&ocirc;n mặt, nụ cười. BL40 nhấn mạnh v&agrave; khả năng giải tr&iacute;, xem phim, m&aacute;y hỗ trợ định dạng DivX, XviD, &acirc;m thanh Dobly Digital, giắc cắm tai nghe 3,5 mm.</p>\r\n<p class=\"Normal\">Ngo&agrave;i ra, model n&agrave;y cũng trang bị kết nối 3G/HSDPA tốc độ 7,2Mb/gi&acirc;y, Wi-Fi, GPS. Dưới đ&acirc;y l&agrave; một số h&igrave;nh ảnh thực tế BL40.</p>\r\n<table border=\"0\" cellspacing=\"2\" cellpadding=\"2\" width=\"77%\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://sohoa.vnexpress.net/Files/Subject/3B/9B/1C/5D/photos-lg-bl40-chocolate-mobile-phone-0.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"480\" /></td>\r\n</tr>\r\n<tr>\r\n<td><img src=\"http://sohoa.vnexpress.net/Files/Subject/3B/9B/1C/5D/photos-lg-bl40-chocolate-mobile-phone-1.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"321\" /></td>\r\n</tr>\r\n<tr>\r\n<td><img src=\"http://sohoa.vnexpress.net/Files/Subject/3B/9B/1C/5D/photos-lg-bl40-chocolate-mobile-phone-2.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"321\" /></td>\r\n</tr>\r\n<tr>\r\n<td><img src=\"http://sohoa.vnexpress.net/Files/Subject/3B/9B/1C/5D/photos-lg-bl40-chocolate-mobile-phone-3.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"321\" /></td>\r\n</tr>\r\n<tr>\r\n<td><img src=\"http://sohoa.vnexpress.net/Files/Subject/3B/9B/1C/5D/photos-lg-bl40-chocolate-mobile-phone-4.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"321\" /></td>\r\n</tr>\r\n<tr>\r\n<td><img src=\"http://sohoa.vnexpress.net/Files/Subject/3B/9B/1C/5D/photos-lg-bl40-chocolate-mobile-phone-5.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"321\" /></td>\r\n</tr>\r\n<tr>\r\n<td><img src=\"http://sohoa.vnexpress.net/Files/Subject/3B/9B/1C/5D/photos-lg-bl40-chocolate-mobile-phone-6.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"321\" /></td>\r\n</tr>\r\n<tr>\r\n<td><img src=\"http://sohoa.vnexpress.net/Files/Subject/3B/9B/1C/5D/lg-bl40-chocolate-video-photos-1.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"360\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><strong>&nbsp;</strong></p>', 406, 200, NULL, 329, 1, 1),
(635, 45, 1, 'Dell Venue Pro', ' Dell Venue Pro  \r\n- Hệ điều hành Microsoft Windows Phone 7\r\n- CPU: Qualcomm Snapdragon QSD8250, 1 GHz processor\r\n- Máy ảnh 5.0 MP, lấy nét tự động\r\n- Mạng 3G với HSDPA, 7.2 Mbps; HSUPA, 5.76 Mbps\r\n- Hỗ trợ Full flash/silverlight ', '0000-00-00', 7524000, 7524000, 'dell_pro_b.jpg', '<h2>Dell tr&igrave;nh l&agrave;ng Venue Pro chạy Windows Phone 7</h2>\r\n<div class=\"news_description\">Ngo&agrave;i HTC HD7 th&igrave; nh&agrave; mạng T-Mobile  c&ograve;n d&agrave;nh th&ecirc;m quyền ph&acirc;n phối một smartphone chạy Windows Phone 7 kh&aacute;c  đ&oacute; l&agrave; Dell Venue Pro. Venue Pro c&ograve;n biết tới trước đ&acirc;y với c&aacute;i t&ecirc;n  Lightning.</div>\r\n<p>M&aacute;y c&oacute; m&agrave;n h&igrave;nh kh&aacute; to, thiết kế  trượt với b&agrave;n ph&iacute;m QWERTY đầy đủ, sẽ l&agrave; đối trọng đ&aacute;ng gờm của  BlackBerry Torch 9800.<br /><br />Venue Pro c&oacute; thiết kế kh&aacute; mượt m&agrave; với hai  cạnh tr&aacute;i v&agrave; phải được bo tr&ograve;n, cạnh tr&ecirc;n v&agrave; dưới được v&aacute;t ch&eacute;o xuống.  V&igrave; được t&iacute;ch hợp th&ecirc;m b&agrave;n ph&iacute;m QWERTY n&ecirc;n tr&ocirc;ng m&aacute;y kh&aacute; d&agrave;y. Ở cạnh tr&aacute;i  của Venue Pro, ch&uacute;ng ta kh&ocirc;ng hề thấy bất kỳ ph&iacute;m bấm n&agrave;o trong khi  cạnh phải c&oacute; n&uacute;t bấm chụp ảnh v&agrave; n&uacute;t tăng, giảm &acirc;m lượng. Ấn tượng nhất  c&oacute; lẽ l&agrave; thiết kế mặt sau m&aacute;y với c&aacute;c hoa văn in ch&igrave;m. Giống như tr&ecirc;n  c&aacute;c MTXT hiệu Dell, logo Dell với v&ograve;ng tr&ograve;n kh&aacute; to được nh&agrave; sản xuất gắn  ph&iacute;a sau Venue Pro. Ph&iacute;a dưới logo l&agrave; d&ograve;ng chữ Windows Phone v&agrave; tr&ecirc;n l&agrave;  camera c&ugrave;ng với đ&egrave;n trợ s&aacute;ng.<br /><br />Ch&uacute;ng ta chưa biết nhiều về th&ocirc;ng  số kỹ thuật của Venue Pro thời điểm hiện tại, mặc d&ugrave; đ&atilde; được giới thiệu  ch&iacute;nh thức. Chỉ biết rằng m&aacute;y c&oacute; m&agrave;n h&igrave;nh AMOLED 4,1\" với c&ocirc;ng nghệ  Gorilla Glass cho độ bền v&agrave; khả năng chống xước cao. C&ugrave;ng với đ&oacute; l&agrave; BXL  Snapdragon 1Ghz, camera 5MP. Th&ocirc;ng số kỹ thuật sẽ được cập nhật ngay khi  xuất hiện. Dell Venue Pro sẽ được ph&acirc;n phối bởi T-Mobile tại Mỹ, dự  kiến sẽ bắt đầu b&aacute;n trong dịp nghỉ lễ cuối năm.<br /><br /></p>\r\n<div><img title=\"Dell tr&igrave;nh l&agrave;ng Venue Pro chạy Windows Phone 7\" src=\"http://images.thegioididong.com/Files/2010/10/12/22640/406_Dell-trinh-lang-Venue-Pro-chay-Windows-Phone-7.jpg\" border=\"0\" alt=\"Dell tr&igrave;nh l&agrave;ng Venue Pro chạy Windows Phone 7\" /><br /><br /><img title=\"Dell tr&igrave;nh l&agrave;ng Venue Pro chạy Windows Phone 7\" src=\"http://images.thegioididong.com/Files/2010/10/12/22640/407_Dell-trinh-lang-Venue-Pro-chay-Windows-Phone-7.jpg\" border=\"0\" alt=\"Dell tr&igrave;nh l&agrave;ng Venue Pro chạy Windows Phone 7\" /><br /><br />\r\n<div><em>Một số h&igrave;nh ảnh tr&ecirc;n tay từ Engadget:<br /><br /></em>\r\n<div><img title=\"Dell tr&igrave;nh l&agrave;ng Venue Pro chạy Windows Phone 7\" src=\"http://images.thegioididong.com/Files/2010/10/12/22640/408_Dell-trinh-lang-Venue-Pro-chay-Windows-Phone-7.jpg\" border=\"0\" alt=\"Dell tr&igrave;nh l&agrave;ng Venue Pro chạy Windows Phone 7\" /><br /><br /><img title=\"Dell tr&igrave;nh l&agrave;ng Venue Pro chạy Windows Phone 7\" src=\"http://images.thegioididong.com/Files/2010/10/12/22640/409_Dell-trinh-lang-Venue-Pro-chay-Windows-Phone-7.jpg\" border=\"0\" alt=\"Dell tr&igrave;nh l&agrave;ng Venue Pro chạy Windows Phone 7\" /><br /><br /><img title=\"Dell tr&igrave;nh l&agrave;ng Venue Pro chạy Windows Phone 7\" src=\"http://images.thegioididong.com/Files/2010/10/12/22640/419_Dell-trinh-lang-Venue-Pro-chay-Windows-Phone-7.jpg\" border=\"0\" alt=\"Dell tr&igrave;nh l&agrave;ng Venue Pro chạy Windows Phone 7\" /><br /><br /><img title=\"Dell tr&igrave;nh l&agrave;ng Venue Pro chạy Windows Phone 7\" src=\"http://images.thegioididong.com/Files/2010/10/12/22640/420_Dell-trinh-lang-Venue-Pro-chay-Windows-Phone-7.jpg\" border=\"0\" alt=\"Dell tr&igrave;nh l&agrave;ng Venue Pro chạy Windows Phone 7\" /><br /><br /><img title=\"Dell tr&igrave;nh l&agrave;ng Venue Pro chạy Windows Phone 7\" src=\"http://images.thegioididong.com/Files/2010/10/12/22640/421_Dell-trinh-lang-Venue-Pro-chay-Windows-Phone-7.jpg\" border=\"0\" alt=\"Dell tr&igrave;nh l&agrave;ng Venue Pro chạy Windows Phone 7\" /><br /><br /><img title=\"Dell tr&igrave;nh l&agrave;ng Venue Pro chạy Windows Phone 7\" src=\"http://images.thegioididong.com/Files/2010/10/12/22640/422_Dell-trinh-lang-Venue-Pro-chay-Windows-Phone-7.jpg\" border=\"0\" alt=\"Dell tr&igrave;nh l&agrave;ng Venue Pro chạy Windows Phone 7\" /><br /><br /><img title=\"Dell tr&igrave;nh l&agrave;ng Venue Pro chạy Windows Phone 7\" src=\"http://images.thegioididong.com/Files/2010/10/12/22640/423_Dell-trinh-lang-Venue-Pro-chay-Windows-Phone-7.jpg\" border=\"0\" alt=\"Dell tr&igrave;nh l&agrave;ng Venue Pro chạy Windows Phone 7\" /></div>\r\n</div>\r\n</div>\r\n<p>&nbsp;</p>', 412, 200, NULL, 369, 1, 0),
(636, 38, 1, 'LG GT540 OPTIMUS', 'Hệ điều hành Android 1.6\r\nMáy ảnh 3.0 MP, lấy nét tự động\r\nMáy nghe nhạc MP3/WAV/WMA/eAAC+\r\nKết nối mạng 3G với tốc độ truyền 7.2 Mbps\r\nĐài FM tích hợp, jack tai nghe 3.5mm\r\nHỗ trợ wed xã hội Google Search, Maps, Gmail, YouTube, Google Talk, Facebook', '0000-00-00', 4499000, 4499000, 'LG-GT540_b.jpg', '<p><span style=\"font-size: medium;\"><strong>Đ&acirc;y l&agrave; ch&uacute; dế Android thứ 2 của h&atilde;ng sản xuất điện thoại di động H&agrave;n Quốc.</strong></span></p>\r\n<div class=\"content-noidung\">\r\n<p style=\"margin: 14px 0px; line-height: 18px; text-align: justify;\">C&oacute; lẽ chưa bao giờ người d&ugrave;ng lại được m&atilde;n nh&atilde;n với nhiều sản phẩm th&uacute; vị v&agrave; hấp dẫn đến thế ở triển l&atilde;m thiết bị điện tử ti&ecirc;u d&ugrave;ng. V&agrave; 1 trong những thiết bị nổi bật nhất tại CES 2010 năm nay ch&iacute;nh l&agrave; LG GT540 Swift.</p>\r\n<p style=\"margin: 14px 0px; line-height: 18px; text-align: justify;\">Đ&acirc;y l&agrave; ch&uacute; dế Android thứ 2 của LG (sau model GW620). Swift c&oacute; thiết kế tương tự như ch&uacute; dế nghe nhạc W995 của Sony Ericsson. Swift c&oacute; 3 phi&ecirc;n bản m&agrave;u l&agrave; đen, trắng v&agrave; hồng.</p>\r\n<p style=\"margin: 14px 0px; line-height: 18px; text-align: justify;\">Ch&uacute; dế n&agrave;y trang bị m&agrave;n h&igrave;nh cảm ứng điện trở, độ ph&acirc;n giải 320 x 480 pixel, camera 3 15 megapixel. Cũng như những ch&uacute; dế ra mắt gần đ&acirc;y, Swift cũng được tập trung v&agrave;o khả năng hỗ trợ c&aacute;c mạng x&atilde; hội như Facebook, Twitter hay Bebo.</p>\r\n<p style=\"margin: 14px 0px; line-height: 18px; text-align: justify;\">Swift c&oacute; giắc tai nghe 3.5 mm, hỗ trợ chuẩn DivX v&agrave; WMV. Khe cắm microSD cho ph&eacute;p mở rộng bộ nhớ l&ecirc;n tới 32 GB.</p>\r\n</div>', 440, 200, NULL, 357, 1, 0),
(637, 45, 1, 'Dell Venue ', ' Dell Venue  \r\n- Hệ điều hành Android v2.1\r\n- CPU: Qualcomm Snapdragon QSD8250, 1 GHz processor\r\n- Máy ảnh 8MP, hỗ trợ LED Flash\r\n- Mạng 3G, Wifi giúp dễ dàng truy cập internet mọi lúc mọi nơi\r\n- Hỗ trợ Full Flash\r\n- YouTube, Google Talk, Picasa integrati', '0000-00-00', 9875000, 9875000, 'Dell_thunder_b.jpg', '<div class=\"productIntro\">\r\n<div class=\"introProduct\">\r\n<div class=\"sectionHeader\">\r\n<h2 class=\"cufonGothamMedium\">Smart. Any way you slide it.</h2>\r\n</div>\r\n<div id=\"bazaarVoice\">\r\n<div id=\"BVContainerPageURL\" style=\"display: none;\">http://www.dell.com/us/p/product-review/mobile-venue-pro.aspx?productcategory=all-products</div>\r\n<div id=\"BVUrl\" style=\"display: none;\">http://reviews.dell.com:80/2341_ng/mobile-venue-pro/reviews.htm?format=embedded</div>\r\n<div id=\"BVCustomerRatings\">\r\n<div class=\"BVRRRootElement\">\r\n<div class=\"BVRRRatingSummary BVRRPrimarySummary BVRRPrimaryRatingSummary\">\r\n<div class=\"BVRRRatingSummaryStyle2\">\r\n<table class=\"BVRRRatingSummaryStyle2Table\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr class=\"BVRRRatingSummaryStyle2Row\">\r\n<td class=\"BVRRRatingSummaryStyle2RatingsCell\">\r\n<div class=\"BVRROverallRatingContainer\">\r\n<div class=\"BVRRRatingContainerStar\">\r\n<div class=\"BVRRRatingEntry BVRROdd\">\r\n<div class=\"BVRRRating BVRRRatingNormal BVRRRatingOverall\">\r\n<div class=\"BVRRRatingNormalImage\"><img title=\"4.2 out of 5\" src=\"http://reviews.dell.com/2341_ng/4_2/5/rating.gif\" alt=\"4.2 out of 5\" /></div>\r\n<div class=\"BVRRRatingNormalOutOf\"><span class=\"BVRRNumber BVRRRatingNumber\">4.2</span> <span class=\"BVRRSeparatorText\">out of</span> <span class=\"BVRRNumber BVRRRatingRangeNumber\">5</span></div>\r\n<div class=\"BVRRLabel BVRRRatingNormalLabel\"><span class=\"BVRRLabelOverall\">Avg Customer Rating</span><span class=\"BVRRLabelRatingsOnly\">Overall Rating</span><span class=\"BVRRRatingNormal